pragma solidity ^0.5.0;

import "./SafeMath.sol";
import "./Ownable.sol";

contract DiceGame is Ownable{ //Ownable allows use onlyOwner modifier so we can make admin functions

	using SafeMath for uint256; //Using SafeMath lib to avoid overflow erros

	//Variables
	struct Bet{
		uint diceBet;
		uint crashBet;
		bool isSet; 	
		uint diceResult;
		uint crashResult;
		uint moneyBet;
	}
	mapping(address => Bet) private betsMap;
	address currentPlayer;
	uint public gameType = 0; //0: null, 1: dice, 2: crash
	uint public maximumBetValue = 1 ether;
	uint public minimumBetValue = 0.001 ether;
	uint private contractBalance = 0;

	//Events
	event EventCancelBet(address player);
	event EventDiceBet(address player , uint diceBet);
	event EventDiceResult(address player, uint diceBet , uint diceResult);
	event EventCrashBet(address player , uint crashBet);
	event EventCrashResult(address player, uint crashBet , uint crashResult);

	//Constructor
	constructor() public{
		currentPlayer = msg.sender;
	}

	//Modifiers
	modifier isRightPlayer(address player){
		require(msg.sender == player);
		_;
	}
	modifier isEnoughMoney(){
		require(msg.value >= minimumBetValue, "Too low bet value");
		require(msg.value <= maximumBetValue, "Too high bet value");
		_;
	}
	modifier currentBetNull(){
		require(betsMap[currentPlayer].isSet == false, "There is already a bet ready");
		_;
	}
	modifier currentBetNotNull(){
		require(betsMap[currentPlayer].isSet == true, "You need to bet before playing"); 
		_;
	}

	//General casino functions
	function playerWin() external isRightPlayer(currentPlayer){
		if(gameType == 1){
			msg.sender.transfer(betsMap[currentPlayer].moneyBet * 10);
		}else if(gameType == 2){
			msg.sender.transfer(betsMap[currentPlayer].moneyBet * betsMap[currentPlayer].crashBet);
		}
	}
	function isBetSet() public view returns(bool){
		return betsMap[currentPlayer].isSet;
	}
	function cancelBet() external isRightPlayer(currentPlayer) returns(bool){
		require(betsMap[currentPlayer].isSet == true, "There is no bet currently");
		betsMap[currentPlayer].diceBet = 0;
		betsMap[currentPlayer].crashBet = 0;
		betsMap[currentPlayer].isSet = false;
		betsMap[currentPlayer].diceResult = 0;
		betsMap[currentPlayer].crashResult = 0;
		msg.sender.transfer(betsMap[currentPlayer].moneyBet);
		betsMap[currentPlayer].moneyBet = 0;
		emit EventCancelBet(currentPlayer);
		return true;
	}

	//Dice game functions
	function getDiceBet(uint playerBet) public payable isRightPlayer(currentPlayer) isEnoughMoney returns(uint, bool, uint){
		require(playerBet >= 2, "Bet must be between 2 and 12");
		require(playerBet <= 12, "Bet must be between 2 and 12");
		contractBalance += msg.value;
		betsMap[currentPlayer].isSet = true;
		betsMap[currentPlayer].diceBet = playerBet;
		emit EventDiceBet(currentPlayer, betsMap[currentPlayer].diceBet);
		return (betsMap[currentPlayer].diceBet, betsMap[currentPlayer].isSet, betsMap[currentPlayer].moneyBet);
	}
	function playDice() public isRightPlayer(currentPlayer) returns(address , uint , uint){
		gameType = 1;																
		betsMap[currentPlayer].diceResult = randomDoubleDice();															
		betsMap[currentPlayer].isSet = false;
		if(betsMap[currentPlayer].diceResult == betsMap[currentPlayer].diceBet){	
			this.playerWin();
		}
		emit EventDiceResult(currentPlayer, betsMap[currentPlayer].diceBet, betsMap[currentPlayer].diceResult);
		return (currentPlayer , betsMap[currentPlayer].diceBet , betsMap[currentPlayer].diceResult);
	}
    function randomDoubleDice() private view returns (uint){
    	uint randomId = 0;
		uint firstDice = uint(keccak256(abi.encodePacked(now, currentPlayer, randomId))) % 6 + 1;	
		randomId++;
		uint secondDice = uint(keccak256(abi.encodePacked(now, currentPlayer, randomId))) % 6 + 1;
		return firstDice + secondDice;
    }

    //Crash game functions
	function getCrashBet(uint playerBet) public payable isRightPlayer(currentPlayer) isEnoughMoney currentBetNull returns(uint, bool, uint){
		require(playerBet > 0, "Bet must be positive");
		contractBalance += msg.value;
		betsMap[currentPlayer].isSet = true;
		betsMap[currentPlayer].crashBet = playerBet;
		emit EventCrashBet(currentPlayer, betsMap[currentPlayer].crashBet);
		return (betsMap[currentPlayer].crashBet, betsMap[currentPlayer].isSet, betsMap[currentPlayer].moneyBet);
	}
	function playCrash() public isRightPlayer(currentPlayer) currentBetNotNull returns(address , uint , uint){	
		gameType = 2;																
		betsMap[currentPlayer].crashResult = crashGame();															
		betsMap[currentPlayer].isSet = false;
		if(betsMap[currentPlayer].crashResult >= betsMap[currentPlayer].crashBet){	
			this.playerWin();
		}
		emit EventCrashResult(currentPlayer, betsMap[currentPlayer].crashBet, betsMap[currentPlayer].crashResult);
		return (currentPlayer , betsMap[currentPlayer].crashBet , betsMap[currentPlayer].crashResult);
	}
	function crashGame() private view returns(uint){
		return 2;
	}

    //Admin functions with onlyOwner
    function addFundsContractBalance() external payable onlyOwner{
    	require(msg.value > 0, "No funds transfered");
		contractBalance += msg.value;
	}
    function withdrawContractBalance() external onlyOwner{
		msg.sender.transfer(contractBalance);
	}
	function getContractBalance() external view onlyOwner returns(uint){
		return contractBalance;
	}

}
